export default {
  data: function data() {
    return {
      sign: 0,
      userid: 1,
      length: 0,
      center: [108.152601, 30.193629],
      path: [],
      markerList: [],
      map: "",
      geocoder: "",
      polyline: "",
      status: [],
      expressStatus: "",
      isCollect: 0,
      id: "11111111111111111111111",
      expresscompany: "",
      messages: [],
      address: ["北京市朝阳区阜荣街10号"],
      response: {
        Traces: [{
          AcceptStation: "【山东省市场部中心】 已收件 取件人。。",
          AcceptTime: "2020-12-10 21:00:53"
        }, {
          AcceptStation: "【济南转运中心公司】 已收件 取件人。。",
          AcceptTime: "2020-12-11 18:00:53"
        }, {
          AcceptStation: "【浦东转运中心公司】 已收件 取件人。。",
          AcceptTime: "2020-12-11 21:00:53"
        }]
      }
    };
  },
  methods: {
    getQuery: function getQuery() {
      this.id = this.$route.query.ExpressInfoExpressid;
      this.expresscompany = this.$route.query.ExpressInfoExpressCompany;
    },
    geoCode: function geoCode(address, i) {
      var _this = this;

      this.geocoder.getLocation(address, function (status, result) {
        if (status === "complete" && result.geocodes.length) {
          var lnglat = result.geocodes[0].location;
          var temp = [lnglat.lng, lnglat.lat];

          _this.path.push(temp); // this.polyline.setPath(this.path);


          var marker = new AMap.Marker();
          marker.setPosition(lnglat);

          _this.markerList.push(marker);

          _this.map.add(marker);
        } else {
          console.log("根据地址查询位置失败");
          _this.length--;
        }

        if (_this.path.length == _this.length && i == _this.address.length - 1) {
          _this.polyline.setPath(_this.path);

          _this.map.setFitView([_this.polyline]);
        }

        if (i < _this.address.length - 1) {
          _this.geoCode(_this.address[i + 1], i + 1);
        }
      });
    },
    processData: function processData() {
      switch (this.response.State) {
        case "0":
          break;

        case "1":
          this.expressStatus = "未发货";
          break;

        case "2":
          this.expressStatus = "运输中";
          break;

        case "3":
          this.expressStatus = "已签收";
          break;

        case "4":
          break;
      }

      for (var i = 0; i < this.response.Traces.length; i++) {
        var str = "";

        for (var j = 0; j < this.response.Traces[i].AcceptStation.length; j++) {
          if (this.response.Traces[i].AcceptStation[j] !== "【" && this.response.Traces[i].AcceptStation[j] !== "】") {
            str += this.response.Traces[i].AcceptStation[j];
          }

          if (this.response.Traces[i].AcceptStation[j] === "】") {
            break;
          }
        }

        this.address[i] = str;
        this.messages[i] = {
          time: {
            time: "",
            date: ""
          },
          info: ""
        };
        var date = this.response.Traces[i].AcceptTime.split(" ");
        this.messages[i].time.date = date[0];
        this.messages[i].time.time = date[1];
        this.messages[i].info = this.response.Traces[i].AcceptStation;
        this.id = this.response.LogisticCode;
        this.$set(this.messages);
      }

      this.messages.reverse(); // for (var i = 0; i < this.address.length; i++) {
      //     this.status[i] = false;
      // }

      this.length = this.address.length; // for (var i = 0; i < this.address.length; i++) {
      //   // console.log(this.address[i]);
      //   // setTimeout(()=>{this.geoCode(this.address[i], i)},100*i);
      //   this.geoCode(this.address[i], i);
      // }

      this.geoCode(this.address[0], 0);
    },
    getInfo: function getInfo(num, id) {
      var _this2 = this;

      var url = "http://localhost:8020/search/search";
      this.$http.get(url, {
        params: {
          num: num,
          cid: id
        }
      }, {
        emulateJSON: true
      }).then(function (response) {
        _this2.response = response.data;
        console.log(response);

        if (_this2.response.Success == true) {
          _this2.processData();
        } else {
          _this2.$alert("无法根据您提供的单号搜索到快递，请检查单号是否输入正确！", "搜索失败", {
            confirmButtonText: "确定",
            callback: function callback(action) {
              _this2.$router.push({
                path: "/SearchExpress"
              });
            }
          });
        }
      });
    },
    iscollect: function iscollect() {
      var _this3 = this;

      var url = "http://localhost:8705/express/ismyexpress";
      this.$http.get(url, {
        params: {
          expressid: this.id,
          userid: this.userid
        }
      }, {
        emulateJSON: true
      }).then(function (response) {
        _this3.isCollect = response.data;
      });
    },
    collect: function collect() {
      var _this4 = this;

      var url = "http://localhost:8705/express/addexpress";
      console.log(this.messages[0].info);
      this.$http.get(url, {
        params: {
          companyid: this.expresscompany,
          expressdetail: this.messages[0].info,
          expressid: this.id,
          expressstatus: this.expressStatus,
          userid: this.userid
        }
      }, {
        emulateJSON: true
      }).then(function (response) {
        if (response.data === 1) {
          _this4.$message({
            showClose: true,
            message: "收藏成功！",
            type: "success"
          });

          _this4.isCollect = 1;
        } else if (response.data === 0) {
          _this4.$message({
            showClose: true,
            message: "收藏失败！",
            type: "erro"
          });
        }
      });
    },
    cancelCollect: function cancelCollect() {
      var _this5 = this;

      var url = "http://localhost:8705/express/deleteexpress";
      this.$http.get(url, {
        params: {
          expressid: this.id,
          userid: this.userid
        }
      }, {
        emulateJSON: true
      }).then(function (response) {
        if (response.data === 1) {
          _this5.$message({
            showClose: true,
            message: "取消收藏成功！",
            type: "success"
          });

          _this5.isCollect = 0;
        } else if (response.data === 0) {
          _this5.$message({
            showClose: true,
            message: "取消收藏失败！",
            type: "erro"
          });
        }
      });
    }
  },
  mounted: function mounted() {
    this.map = new AMap.Map("container", {
      /*showIndoorMap: false,
                          isHotspot: false,
                          zoomEnable: true,
                          keyboardEnable: false,
                          dragEnable: true,
                          showLabel: false,
                          touchZoomCenter: 1,*/
      zoom: 3,
      center: [108.152601, 30.193629]
    });
    this.geocoder = new AMap.Geocoder({});
    this.polyline = new AMap.Polyline({
      path: [],
      isOutline: true,
      outlineColor: "#ffeeff",
      borderWeight: 3,
      strokeColor: "#3366FF",
      strokeOpacity: 1,
      strokeWeight: 3,
      // 折线样式还支持 'dashed'
      strokeStyle: "solid",
      // strokeStyle是dashed时有效
      strokeDasharray: [10, 5],
      lineJoin: "round",
      lineCap: "round",
      zIndex: 50
    });
    this.polyline.setMap(this.map);
    this.getQuery();
    this.getInfo(this.id, this.expresscompany);
    this.iscollect();
  }
};
